import React from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

const educationData = [
  {
    year: '2023',
    school: 'University of Technology',
    degree: 'Bachelor of Science in Computer Science',
    grade: 'First Class Honours',
    results: [
      { subject: 'Programming Fundamentals', grade: 'A+' },
      { subject: 'Data Structures', grade: 'A' },
      { subject: 'Web Development', grade: 'A+' },
      { subject: 'Database Systems', grade: 'A' },
      { subject: 'Software Engineering', grade: 'A-' }
    ]
  },
  {
    year: '2021',
    school: 'High School',
    degree: 'High School Diploma',
    grade: 'Distinction',
    results: [
      { subject: 'Mathematics', grade: 'A+' },
      { subject: 'Physics', grade: 'A' },
      { subject: 'Chemistry', grade: 'A-' },
      { subject: 'English', grade: 'A' }
    ]
  }
];

const Education = () => {
  React.useEffect(() => {
    AOS.init({
      duration: 1000,
      once: true
    });
  }, []);

  return (
    <section id="education" className="education-section">
      <div className="container">
        <h2 data-aos="fade-up">Education</h2>
        <div className="education-timeline">
          {educationData.map((edu, index) => (
            <div key={index} className="education-item" data-aos="fade-up" data-aos-delay={index * 100}>
              <div className="education-year">{edu.year}</div>
              <div className="education-content">
                <h3>{edu.school}</h3>
                <p className="degree">{edu.degree}</p>
                <p className="grade">{edu.grade}</p>
                <div className="results-table">
                  <table>
                    <thead>
                      <tr>
                        <th>Subject</th>
                        <th>Grade</th>
                      </tr>
                    </thead>
                    <tbody>
                      {edu.results.map((result, resultIndex) => (
                        <tr key={resultIndex}>
                          <td>{result.subject}</td>
                          <td>{result.grade}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Education;
